import React, { useState, useEffect } from 'react';
import ChatHistory from './components/ChatHistory';
import ChatInput from './components/ChatInput';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [chats, setChats] = useState([]);
  const [currentChat, setCurrentChat] = useState(null);

  const handleSend = (message) => {
    if (currentChat !== null) {
      const updatedChats = [...chats];
      updatedChats[currentChat].messages.push(message);
      setChats(updatedChats);
      setMessages(updatedChats[currentChat].messages);
    }
  };

  const handleNewChat = () => {
    const newChat = { id: chats.length, messages: [] };
    setChats([...chats, newChat]);
    setCurrentChat(newChat.id);
    setMessages([]);
  };

  const handleSelectChat = (chatId) => {
    setCurrentChat(chatId);
    setMessages(chats[chatId].messages);
  };

  useEffect(() => {
    const handleBeforeUnload = async () => {
      await fetch('/api/cleanup', { method: 'POST' });
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  return (
    <div className="app">
      <div className="sidebar">
        <button onClick={handleNewChat}>New Chat</button>
        <ChatHistory chats={chats} onSelectChat={handleSelectChat} />
      </div>
      <div className="main">
        <div className="chat-window">
          {currentChat !== null && (
            <>
              {messages.map((message, index) => (
                <div key={index} className="message">{message}</div>
              ))}
            </>
          )}
        </div>
        <ChatInput onSend={handleSend} />
      </div>
    </div>
  );
};

export default App;
